$(document).ready(function(){$(".slider").slick({autoplay:!0,autoplaySpeed:2e3,speed:1e3,dots:!0,arrows:!1,pauseOnFocus:!1,pauseOnHover:!1})});const anchors=document.querySelectorAll('a[href*="#"]'),arrowDown=document.querySelectorAll("arrow-dwn");anchors.forEach(e=>{const o=e.getAttribute("href");$(e).on("click",function(e){e.preventDefault(),$("html,body").stop().animate({scrollTop:$(o).offset().top},1e3),e.preventDefault()})});const headerTop=document.querySelector(".header__top"),header=document.querySelector(".header");window.addEventListener("scroll",()=>{var e=window.scrollY,o=header.offsetHeight/2;o<e?headerTop.classList.add("fixed"):headerTop.classList.remove("fixed"),o<e&&(bookTableModal.style.display="none")});const bookTB=document.querySelector(".bookTB"),bookTableModal=document.querySelector(".booktable__modal"),bookTableCrestik=document.querySelector(".crestik");bookTB.addEventListener("click",e=>{e.preventDefault(),bookTableModal.style.display="flex",document.querySelector("body").classList.add("lock")}),bookTableCrestik.addEventListener("click",e=>{e.preventDefault(),bookTableModal.style.display="none",document.querySelector("body").classList.remove("lock")});const listOFmenu=document.querySelector(".delicious__menu-items"),bloksItem=document.querySelectorAll(".delicious__menu-products-item");function filter(){listOFmenu.addEventListener("click",e=>{var o=e.target.dataset.id;switch(console.log(o),o){case"all":getItems("delicious__menu-products-item");break;case"soupe":case"pizza":case"pasta":case"desert":case"wine":case"beer":case"drinks":getItems(o)}})}function getItems(o){bloksItem.forEach(e=>{e.classList.contains(o)?e.style.display="block":e.style.display="none"})}filter();